SELECT IF.ID, BR.PRICE, IF.SCREEN, IF.VGA, IF.WEIGHT, FROM INFORMATION IF JOIN POINTLAPTOP PL ON IF.ID = PL.ID JOIN BRAND BR ON PL.ID = BR.ID WHERE 20000000 <= BR.PRICE and BR.PRICE <= 25000000 ORDER BY PL.GAME, PL.VGA DESC LIMIT 5
